package Basis.Func02;

public class Main584 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		showCal();

	}

	private static void showCal() {
		// TODO Auto-generated method stub
		
		for(int i=1;i<4;i++){
			
			for(int k=1;k<4;k++){
				
				System.out.println(i+" + "+k+" = "+(i+k));
				
			}
			
		}
		
	}

}
