package Basis.Operation;

import java.util.Scanner;

public class Main114 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner in = new Scanner(System.in);
		
		int a = in.nextInt();
		int b = in.nextInt();
		
		
		System.out.printf("%d %d\n",++a,b--);
		System.out.printf("%d %d\n",a,b);
		
		
	}

}
